INSERT INTO sentences VALUES(4203,'Ha! Well, there is nothing very instructive in all this.');
INSERT INTO sentences VALUES(4204,'I think that I must turn to you, Watson, for something more solid.');
INSERT INTO sentences VALUES(4205,'I have very little difficulty in finding what I want, said I, for the facts are quite recent, and the matter struck me as remarkable.');
