INSERT INTO sentences VALUES(4226,'George’s, Hanover Square, that only half a dozen intimate friends would be invited, and that the party would return to the furnished house at Lancaster Gate which has been taken by Mr. Aloysius Doran.');
INSERT INTO sentences VALUES(4227,'Two days later—that is, on Wednesday last—there is a curt announcement that the wedding had taken place, and that the honey-moon would be passed at Lord Backwater’s place, near Petersfield.');
